mapper.select:
select d.data_id,d.data_uuid,d.data_date,d.data_applicant,d.data_company,d.data_borough,d.data_street,d.data_type,d.data_level,d.data_theme,d.data_desc,d.data_flag,d.data_river,d.data_update_date, a.audit_status from dm_data d left join dm_audit a on d.data_id = a.data_id where d.data_flag = '0'
AND d.data_theme like concat(concat('%',#{dataTheme}),'%')
AND d.data_type = #{dataType}
AND d.data_borough like concat(concat('%',#{dataBorough}),'%')
AND d.data_river like concat(concat('%',#{dataRiver}),'%')
AND a.audit_status = #{audit.auditStatus}
AND d.data_date >= to_date(#{params.beginTime},'yyyy-MM-dd HH24:mi:ss')
AND d.data_date <= to_date(#{params.endTime},'yyyy-MM-dd HH24:mi:ss')
${params.dataScope}

mapper.update:
update dm_data
data_applicant = #{dataApplicant}, data_company = #{dataCompany}, data_borough = #{dataBorough}, data_street = #{dataStreet}, data_type = #{dataType}, data_level = #{dataLevel}, data_theme = #{dataTheme}, data_desc = #{dataDesc}, data_update_date = sysdate
where data_id = #{dataId}

mapper.delete:
delete from dm_data d
where d.data_id in

	#{dataId}
