mapper.select:
SELECT COUNT(1)
FROM TB_TASK




     TB_TASK.TASK_REGION = #{taskRegion}


    AND TB_TASK.TASK_STREET = #{taskStreet}


    and TB_TASK.TASK_TIME = #{taskTime}

SELECT COUNT(1)
FROM TB_TASK




    TB_TASK.TASK_REGION = #{taskRegion}


    AND TB_TASK.TASK_STREET = #{taskStreet}


    and TB_TASK.TASK_TIME like concat(#{taskTime},'%')
